mapper.update:
UPDATE task SET checkcount = checkcount + 1
where
    taskName=#{taskName} AND company = #{company} AND inspectionType = #{inspectionType};

UPDATE task SET checkcount = checkcount + 1, hiddenDangerCount = hiddenDangerCount + 1
where
    taskName=#{taskName} AND company = #{company} AND inspectionType = #{inspectionType};
